mapper.select:
SELECT

FROM mht_portal.portal_user_app_record a 

WHERE a.id = #{id}

SELECT
FROM mht_portal.portal_user_app_record a
WHERE a.create_date BETWEEN #{beginDate} AND #{endDate}
AND um.name LIKE '%'||#{user.name}||'%' CONCAT('%', #{user.name}, '%') or um.login_name LIKE '%'||#{user.name}||'%' CONCAT('%', #{user.name}, '%')
AND so.name LIKE CONCAT('%', #{office.name}, '%')
AND a.app_id = #{frontApp.id}
AND a.user_id = #{user.id}
ORDER BY a.create_date DESC
