mapper.select:
SELECT 

FROM mht_portal.cms_banner a
WHERE a.id = #{id}

SELECT 

FROM mht_portal.cms_banner a

	a.disable = #{disable}
	AND a.del_flag = #{DEL_FLAG_NORMAL}

ORDER BY a.sort DESC
LIMIT 5

SELECT 

FROM mht_portal.cms_banner a

	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.sort ASC

SELECT 

FROM mht_portal.cms_banner a

	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE mht_portal.cms_banner SET 	
	sort = #{sort},
	description = #{description},
	disable = #{disable},
	image_url = #{imageUrl}
WHERE id = #{id}

UPDATE mht_portal.cms_banner SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
