mapper.select:
SELECT 

FROM mht_portal.cms_index_show_education c
WHERE c.id = #{id}

mapper.update:
UPDATE mht_portal.cms_index_show_education SET 
	analysis_id = #{analysisDepot.id}, 
	education_title = #{title}, 
	education_xAxis = #{xAxis}, 
	education_legend = #{legend}, 
	education_series= #{series}
	update_by = #{updateBy.id}, 
	update_date = #{updateDate},
	remarks = #{remarks} 
WHERE id = #{id}
