mapper.select:
SELECT 

FROM mht_portal.cms_index_show_rate c
WHERE c.id = #{id}

mapper.update:
UPDATE mht_portal.cms_index_show_rate SET 
	analysis_id = #{analysisDepot.id}, 
	rate_title = #{title}, 
	rate_xAxis = #{xAxis}, 
	rate_legend = #{legend}, 
	rate_series= #{series}
	update_by = #{updateBy.id}, 
	update_date = #{updateDate},
	remarks = #{remarks} 
WHERE id = #{id}
