mapper.select:
SELECT 

FROM mht_portal.cms_index_show_research cisr
WHERE cisr.id = #{id}

mapper.update:
UPDATE mht_portal.cms_index_show_research SET 
	analysis_id = #{analysisDepot.id}, 
	research_title = #{title}, 
	research_legend = #{legend}, 
	research_series= #{series}
	update_by = #{updateBy.id}, 
	update_date = #{updateDate},
	remarks = #{remarks} 
WHERE id = #{id}
