mapper.select:
SELECT 

FROM mht_portal.cms_index_show_teachers cist
WHERE cist.id = #{id}

mapper.update:
UPDATE mht_portal.cms_index_show_teachers SET 
	analysis_id = #{analysisDepot.id}, 
	teachers_title = #{title}, 
	teachers_legend = #{legend}, 
	teachers_series= #{series}
	update_by = #{updateBy.id}, 
	update_date = #{updateDate},
	remarks = #{remarks} 
WHERE id = #{id}
