mapper.select:
SELECT 

FROM mht_portal.cms_video_type a

WHERE a.disable='1'

AND a.type_name=#{videoType.typeName}


AND a.id = #{videoType.id}

SELECT 

FROM mht_portal.cms_video_type a

WHERE a.id = #{id}

SELECT 

FROM mht_portal.cms_video_type a


	a.del_flag = #{DEL_FLAG_NORMAL} 

		AND a.type_name LIKE 
		'%'||#{typeName}||'%'
		'%'+#{typeName}+'%'
		CONCAT('%', #{typeName}, '%')


		AND a.disable = #{disable} 




		ORDER BY ${page.orderBy}


		ORDER BY a.sort ASC

mapper.update:
UPDATE mht_portal.cms_video_type SET 	
	type_name = #{typeName},
	sort = #{sort},
	disable = #{disable}
WHERE id = #{id}

UPDATE mht_portal.cms_video_type SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
