mapper.select:
SELECT 

FROM mht_portal.cms_front_app a
WHERE a.id = #{id}

SELECT 

FROM mht_portal.cms_front_app a
WHERE a.app_url = #{appUrl}

SELECT 

FROM mht_portal.cms_front_app a
WHERE a.app_name = #{appName}

SELECT
FROM mht_portal.cms_front_app a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.app_name LIKE '%'||#{appName}||'%' CONCAT('%', #{appName}, '%')
AND a.disable = #{disable}
ORDER BY a.sort ASC

mapper.update:
UPDATE mht_portal.cms_front_app SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
